import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [posts, setPosts] = useState([]);
  const [getTopic, setTopic] = useState('');
  const [getData, setData] = useState('');
  useEffect(() => {
    fetch('http://localhost:3000/getposts')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setPosts(data.posts)
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);

      });
  }, []); 
  return (
    <div className="App">
      <h3>all my posts:</h3>
      <ul>
        {posts.map(post => (
          <ul key={post.ID}>
            {post.ID} topic: {post.topic} data:{post.data}
          </ul>
        ))}
      </ul>

      <>
        <h3> AddPosts </h3>
        <div>

        <input type="text" placeholder="topic" value={getTopic} 
        onChange={e => setTopic(e.target.value)} /><br/>
        <input type="text" placeholder="Time" value={getData} 
        onChange={e => setData(e.target.value)} />
        </div>
        <button onClick={(e) => {
            fetch('http://localhost:3000/addpost', {method: 'POST', body: `topic=${getTopic}&data=${getData}`,
            headers: {'Content-type': 'application/x-www-form-urlencoded'}})
            .then(fetch('http://localhost:3000/addpost')
            .then(response => response.json())
            .then(alert(`new post added`))
            );
        }
    }> Submit</button>
    </>

    </div>
  );
}

export default App;
