        },
        body: new URLSearchParams ({username:username,password:password}),
      })

      .then(response=>response.json())
      .then(alert("new users added"))
      .catch(err => console.error(err))
      setUsername("");
      setPassword("");
      navigate("/channels")
     
      }
        const handleLogIn = () => {
          fetch("http://localhost:3000/login", {
              method: "POST",
