import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link
import"./Channels.css"
export const Channels = () => {
    const [channels,setChannels] = useState([]);
    const [channel,setChannel] = useState("");
    // handle receive.
  
    useEffect(() => {
        fetch('http://localhost:3000/getChannels')
          .then(response => {
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          
          .then(data => {
            if (data && data.channels) { // Check if data has channels
              setChannels(data.channels);
          }
            // setChannels(data.channels)
          })    
          .catch(error => {
            console.error('There was a problem with the fetch operation:', error);
            
          });
      }, []); 


      const createChannel = () => {
        // console.log("New channel before fetch:", newChannel); 
        fetch('http://localhost:3000/addChannel', {
          method: 'POST', 
          body: new URLSearchParams ({channel : channel
        }),
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          },
          
        })
        .then(response => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.text();
        })
        .then(() => {
          alert('new channel added');
          const updatedchannels = [...channels, { channel: channel }]; // Create a new messa object if necessary
          setChannels(updatedchannels); // Update the messas state
          setChannel(""); // Clear the new messa input
          console.log(channels)
        })
        .catch(error => console.error(error));
      }
        // .then(response => response.json())
       
        // .then(alert(`new channel added`))
        // .catch(error => console.error(error)) 
        //  setNewChannel("");
        // setChannels([...channels,newChannel])  
        // console.log(" all the channels:", channels); 
        
        // }
return (
    
<div className="container"> 
<div className='form' >
<p>Join the channels or create your own channel!</p>

   <input className="input"type="text" placeholder="name your channel" value={channel} 
   onChange={(e) => setChannel(e.target.value)} />
 <button  className="button"onClick={createChannel}>Create Channel</button>
 </div>   
          
<div className='channels'>
 

{/* 
                  {channels.map(channel => (
        <ul key={channel.ID}>  
          <Link to={`/channels/${channel.ID}/${channel.channelname}`}>   </Link>
          {channel.channelname}
        </ul>
      ))} */}
       {channels}
  {/* {channels && channels.map((channel, index) => (
                    <div key={index}>
                        <h2>Channel: {channel.channelname}</h2>
                    </div>
                ))} */}
</div>

      


</div>
)


}
export default Channels;
