import React from 'react';
import {useState,useEffect} from 'react';

function Messages(){
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState("");
  useEffect(() => {
    fetch('http://localhost:3000/getposts')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setPosts(data.posts)
      })
      .catch(error => {
        console.error('There was a problem with the fetch operation:', error);
        
      });
  }, []); 

  // const handleNewPost = () => {
  //   fetch('http://localhost:3000/addpost', {
  //     method: 'POST', 
  //     body: new URLSearchParams ({post:post}),
  //     headers: {
  //       'Content-Type': 'application/x-www-form-urlencoded'
  //     },
      
  //   })
  //   .then(response => response.json())
  //   .then(alert(`new post added`))
  //   .catch(error => console.error(error))
   
  //   setPost("")
  //   setPosts([...posts,post])  
  //   // console.log(posts)

    
  //   }
  const handleNewPost = () => {
    fetch('http://localhost:3000/addpost', {
      method: 'POST', 
      body: new URLSearchParams({ post: post }),
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },  
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.text();
    })
    .then(() => {
      alert('new post added');
      const updatedPosts = [...posts, { post: post }]; // Create a new post object if necessary
      setPosts(updatedPosts); // Update the posts state
      setPost(""); // Clear the new post input
    })
    .catch(error => console.error(error));
  }
  

    return ( 
      
      <div className='container'>

        <div className='form'>
      <h3> share your thoughts here </h3>

      <input className="input" type="text" placeholder="content" value={post} 
      onChange={e => setPost(e.target.value)} />
      
      <button  onClick={handleNewPost}>submit</button>
      </div>
    
    <div className='messages'>
     <ul>
      {posts.map(post => (
        <ul key={post.ID}>
          {post.ID}  {post.post}
        </ul>
      ))}
    </ul> 
    </div>



</div>
    
  

 );

}
export default Messages;
